javascript:(async function(){const encodedManifestUrl=encodeURIComponent(window.location.href);const annotationUrl=`https://annotations.allmaps.org/?url=${encodedManifestUrl}`;const editorBase='https://editor.allmaps.org/#/collection?url=';const response=await fetch(annotationUrl).then(response=>response.json());if(response.error){if(window.confirm('Annotation not found. Would you like to open the manifest in the Allmaps Editor?')){location=editorBase+encodedManifestUrl}}else{location=annotationUrl}})();
